    const jestConfig = {
      testMatch: [this.config.testPatterns.unit],
      collectCoverage: true,
      collectCoverageFrom: [
        'src/**/*.{js,ts}',
        '!src/**/*.test.{js,ts}',
        '!src/**/*.spec.{js,ts}',
        '!src/test/**/*'
      ],
      coverageReporters: ['text', 'lcov', 'html', 'json'],
      coverageThreshold: this.config.coverageThreshold,
