          labelNames: ['method', 'route', 'status_code', 'version'],
          buckets: [0.1, 0.3, 0.5, 0.7, 1, 3, 5, 7, 10]
        });

        // Request counter
        this.httpRequestsTotal = new prometheus.Counter({
          name: 'http_requests_total',
          help: 'Total number of HTTP requests',
          labelNames: ['method', 'route', 'status_code', 'version']
        });

        // Active connections gauge
        this.activeConnections = new prometheus.Gauge({
          name: 'http_active_connections',
          help: 'Number of active HTTP connections'
        });

        // Error rate counter
        this.httpErrorsTotal = new prometheus.Counter({
          name: 'http_errors_total',
          help: 'Total number of HTTP errors',
          labelNames: ['method', 'route', 'status_code', 'error_type']
        });
      }

      middleware() {
        return (req, res, next) => {
          const startTime = Date.now();
          this.activeConnections.inc();

          res.on('finish', () => {
            const duration = (Date.now() - startTime) / 1000;
            const route = req.route?.path || req.path;
            const version = req.apiVersion || 'unknown';

            const labels = {
              method: req.method,
              route: route,
              status_code: res.statusCode,
              version: version
            };

            // Record metrics
            this.httpRequestDuration.observe(labels, duration);
            this.httpRequestsTotal.inc(labels);
            this.activeConnections.dec();

            // Record errors
            if (res.statusCode >= 400) {
              this.httpErrorsTotal.inc({
                ...labels,
                error_type: this.getErrorType(res.statusCode)
              });
            }

            // Log slow requests
            if (duration > 1) {
              console.warn('Slow request detected:', {
                method: req.method,
                url: req.url,
                duration: duration,
                statusCode: res.statusCode
              });
            }
          });

          next();
        };
      }

      getErrorType(statusCode) {
        if (statusCode >= 400 && statusCode < 500) {
          return 'client_error';
        } else if (statusCode >= 500) {
          return 'server_error';
        }
        return 'unknown';
      }

      getMetrics() {
        return prometheus.register.metrics();
      }
    }

    module.exports = new ApiAnalytics();
    ```

